/* eslint-disable array-callback-return */
import { CountryContext, useCountry } from "../contexts/CountryContext";
import { useContext } from "react";

function CountryComponent() {
  const { countries, setSelCountry } = useContext(CountryContext);

  const handleChange = (e) => {
    setSelCountry(e.target.value);
  };

  return (
    <div>
      <br />
      Ülke Seç
      <br />
      <select onChange={handleChange}>
        {countries.map((country, index) => (
          <option
            key={country.UlkeID || index}
            value={country.UlkeID}
            selected={country.UlkeID === "2"}
          >
            {country.UlkeAdiEn}
          </option>
        ))}
      </select>
    </div>
  );
}

export default CountryComponent;
