/* eslint-disable array-callback-return */
import { useState } from "react";
import { useCountry } from "../contexts/CountryContext";

function CountryComponent() {
  const { countries, selCountry, setSelCountry } = useCountry();

  const handleChange = (e) => {
    setSelCountry(e.target.value);
  };

  console.log(selCountry);
  return (
    <div>
      <br />
      Ülke Seç
      <br />
      <select
        onChange={handleChange}
        defaultValue={countries.UlkeAdiEn === "TURKEY"}
      >
        {" "}
        {countries.map((country, index) => (
          <option key={country.UlkeID || index} value={country.UlkeID}>
            {country.UlkeAdiEn}
          </option>
        ))}
      </select>
      <br />
      <br />
      Şehir Seç
      <br />
      <select
        onChange={handleChange}
        defaultValue={countries.UlkeAdiEn === "TURKEY"}
      >
        {countries.map((country, index) => (
          <option key={country.UlkeID || index}>{country.UlkeAdiEn}</option>
        ))}
      </select>
    </div>
  );
}

export default CountryComponent;
